import { useState } from 'react';
import '../css/Popup.css';
import ItemImage from './ItemImage.js';

export default function Popup(
    {   inactive,
        state,
        receiveData,
        sendData
    }) {
    const closePopup=()=>{
        inactive(0);
    }
    const handleItemChange = ()=>{

    };
    let buy = <></>
    if(receiveData["buy_price"] == -1){
        buy = <div className=''>
            <input placeholder='Input buy price'/>
            <input placeholder='Input City buy'/>
        </div>
    }else{
        buy =  <div className=''>
            <p>{receiveData["buy_price"]}</p>
        </div>
    }
    let sell = <></>
    if(receiveData["sell_price"] == -1){
        sell = <div className=''>
            <input placeholder='Input sell price'/>
            <input placeholder='Input City sell'/>
        </div>
    }else{
        sell =  <div className=''>
            <p>{receiveData["sell_price"]}</p>
        </div>
    }
    let recipe_buy = []
    if(receiveData["recipe"] != undefined){
        receiveData["recipe"]["buy"].forEach(element => {
            if(element[3] == -1){
                recipe_buy.push(
                    <div>
                        <ItemImage id={element[1]} ign={element[2]}/>
                        <input placeholder='buy'/>
                        <input placeholder='city buy'/>
                    </div>  
                );
            }
        });
    }
    let recipe_craft = []
    if(receiveData["recipe"] != undefined){
        receiveData["recipe"]["craft"].forEach(element => {
            if(element[3] == -1){
                recipe_craft.push(
                    <div>
                        <ItemImage id={element[1]} ign={element[2]}/>
                        <input placeholder='craft'/>
                        <input placeholder='city buy'/>
                    </div>  
                );
            }
        });
    }
    return (
        <>
            <div className={`container-fluid panel-parent ${state==0?"d-none":""}`}>
                <div className='panel'>
                    <div className='container'>
                        <button className='btn close-btn' onClick={closePopup}>X</button>
                        <div className='row'>
                            <div className='col d-felx justify-content-center'>
                                <ItemImage id={receiveData["id"]} ign={receiveData["ign"]}/>
                            </div>
                        </div>
                        <div className='row'>
                            <div className='col'>

                            </div>
                        </div>
                        <div className='d-flex flex-row'>
                            {buy}
                            {sell}
                        </div>
                        <div className='d-flex flex-row'>
                        {recipe_buy}
                        {recipe_craft}
                        </div>

                        <button className='btn' onClick={handleItemChange}>Modify</button>
                    </div>
                </div>
            </div>
        </>
    )

}
