import { useState } from 'react';
import '../css/Popup.css';
import ItemImage from './ItemImage.js';
import { compute_items } from '../scripts/new_final_json.js';

export default function Popup(
    {   inactive,
        state,
        receiveData,
        sendData
    }) {
    const [inputValues, setInputValues] = useState({});

    const closePopup = () => {
        inactive(0);
    };

    const handleInputChange = (event, key) => {
        const { name, value } = event.target;
        setInputValues({
            ...inputValues,
            [key]: {
                ...inputValues[key],
                [name]: value
            }
        });
    };

    const handleItemChange = () => {
        let ceva= localStorage.getItem("jObject")
        // receiveData["recipe"]["buy"].forEach((item)=>{
        //     for(const key in inputValues){
        //         if(key.includes(item[0])){
        //             item[2] = inputValues[key]["city_buy"];
        //             item[3] = inputValues[key]["buy"];  
        //         }
        //     }
        // });
        // console.log(receiveData["recipe"]["buy"]);
        // receiveData["recipe"]["craft"].forEach((item)=>{
        //     for(const key in inputValues){
        //         if(key.includes(item[0])){
        //             item[2] = inputValues[key]["city_buy"];
        //             item[3] = inputValues[key]["buy"];  
        //         }
        //     }
        // });
        console.log(receiveData["recipe"]["craft"]);
        //sendData(inputValues);
        compute_items(receiveData);
        inactive(0);
    };

    let recipe_buy = [];
    if (receiveData["recipe"] !== undefined) {
        receiveData["recipe"]["buy"].forEach(element => {
            if (element[3] === -1) {
                const key = `${element[0]}-buy`;
                recipe_buy.push(
                    <tr className='tr-height' key={key}>
                        <td className='d-flex p-0'>
                            <ItemImage class={"mini-img"} id={element[0]} ign={element[1]} />
                            <h3 className='h3-head mt-auto mb-auto'>{element[1]}</h3>
                        </td>
                        <td className='position-relative p-0'>
                            <input 
                                name="buy"
                                value={inputValues[key]?.buy || ''} 
                                className='inp-field' 
                                placeholder='Price Buy'
                                onChange={(e) => handleInputChange(e, key)} />
                        </td>
                        <td className='position-relative p-0'>
                            <select 
                                name="city_buy"
                                value={inputValues[key]?.city_buy || ''} 
                                class="inp-field"
                                onChange={(e) => handleInputChange(e, key)}>
                                
                                <option selected value="Lymhurst" key="Lymhurst">Lymhurst</option>
                                <option value="Bridgewatch" key="Bridgewatch">Bridgewatch</option>
                                <option value="Martlock" key="Martlock">Martlock</option>
                                <option value="Thetford" key="Thetford">Thetford</option>
                                <option value="Forst Sterling" key="Forst Sterling">Forst Sterling</option>
                                <option value="Caerleon" key="Caerleon">Caerleon</option>
                            </select> 
                        </td>
                    </tr>
                );
            }
        });
    }

    let recipe_craft = [];
    if (receiveData["recipe"] !== undefined) {
        receiveData["recipe"]["craft"].forEach((element) => {
            if (element[3] === -1) {
                const key = `${element[0]}-craft`;
                recipe_craft.push(
                    <tr className='tr-height' key={key}>
                        <td className='d-flex p-0'>
                            <ItemImage class={"mini-img"} id={element[0]} ign={element[1]} />
                            <h3 className='h3-head mt-auto mb-auto'>{element[1]}</h3>
                        </td>
                        <td className='position-relative p-0'>
                            <input
                                name="buy"
                                value={inputValues[key]?.buy || ''} 
                                className='inp-field' 
                                placeholder='Price Buy'
                                onChange={(e) => handleInputChange(e, key)}/>
                        </td>
                        <td className='position-relative p-0'>
                            <select 
                            
                                name="city_buy"
                                value={inputValues[key]?.city_buy || ''} 
                                class="inp-field"
                                onChange={(e) => handleInputChange(e, key)}>
                                    
                                <option selected value="Lymhurst" key="Lymhurst">Lymhurst</option>
                                <option value="Bridgewatch" key="Bridgewatch">Bridgewatch</option>
                                <option value="Martlock" key="Martlock">Martlock</option>
                                <option value="Thetford" key="Thetford">Thetford</option>
                                <option value="Forst Sterling" key="Forst Sterling">Forst Sterling</option>
                                <option value="Caerleon" key="Caerleon">Caerleon</option>
                            </select> 
                           
                        </td>
                    </tr>
                );
            }
        });
    }

    return (
        <>
            <div className={`container-fluid panel-parent ${state === 0 ? "d-none" : ""}`}>
                <div className='panel'>
                    <div className='container'>
                        <button className='btn close-btn' onClick={closePopup}>X</button>
                        <div className='row'>
                            <div className='col'> 
                                <ItemImage class={"img-head"} id={receiveData["id"]} ign={receiveData["ign"]} />
                                <h1 className='h1-head'>{receiveData["ign"]}</h1>
                            </div>
                        </div>
                        <div className='row'>
                            <div className='col'> 
                                <div className='container scroll-popup' id='custom-scroll'>
                                    <table className='table table-striped table-dark'>
                                        <thead>
                                            <tr>
                                                <th>
                                                    <h2 className='h2-head'>Item</h2>
                                                </th>
                                                <th>
                                                    <h2 className='h2-head'>Price Buy</h2>
                                                </th>
                                                <th>
                                                    <h2 className='h2-head'>City buy</h2>
                                                </th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {recipe_buy}
                                            {recipe_craft}    
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                        <button className='btn btn-primary modify-btn' onClick={handleItemChange}>Modify</button>
                    </div>
                </div>
            </div>
        </>
    );
}
