import { useState } from 'react';
import '../css/Popup.css';
import ItemImage from './ItemImage.js';
import { compute_item } from '../scripts/new_final_json.js';

export default function Popup(
    {   inactive,
        state,
        receiveData,
        sendData
    }) {
    const [inputValues, setInputValues] = useState({});

    const closePopup = () => {
        inactive(0);
    };

    const handleInputChange = (event, key) => {
        const { name, value } = event.target;
        setInputValues({
            ...inputValues,
            [key]: {
                ...inputValues[key],
                [name]: value
            }
        });
    };

    const handleItemChange = () => {

        for(const key in inputValues){
            const key_ = key.slice(0,key.length -4);
            if(receiveData["recipe"]["buy"][key_] != undefined){
                receiveData["recipe"]["buy"][key_]["price_buy"] = Int32Array(inputValues[key]["buy"])
                receiveData["recipe"]["buy"][key_]["city_buy"] = inputValues[key]["ctb"]
                receiveData["recipe"]["buy"][key_]["city_sell"] = inputValues[key]["cts"]
                receiveData["recipe"]["buy"][key_]["price_sell"] = Int32Array(inputValues[key]["sel"])
            }
            if(receiveData["recipe"]["craft"][key_] != undefined){
                receiveData["recipe"]["craft"][key_]["price_buy"] = Int32Array(inputValues[key]["buy"])
                receiveData["recipe"]["craft"][key_]["city_buy"] = inputValues[key]["ctb"]
                receiveData["recipe"]["craft"][key_]["city_sell"] = inputValues[key]["cts"]
                receiveData["recipe"]["craft"][key_]["price_sell"] = Int32Array(inputValues[key]["sel"])
            }
        }
        console.log(receiveData["recipe"])
        // receiveData["recipe"]["craft"].forEach((item)=>{
        //     for(const key in inputValues){
        //         if(key.includes(item[0])){
