import '../css/InputMenu3.css';
import { useState } from 'react';
import CollapseMenu from './CollapseMenu.js';


export default function InputMenu3(){
    const [inputValues, setInputValues] = useState({});
    let site_input = JSON.parse(localStorage.getItem("site_input"));
    const handleInputChange = (event, key) => {
        const { name, value } = event.target;
        setInputValues({
            ...inputValues,
            [key]: {
                ...inputValues[key],
                [name]: value
            }
        });
    };
    const handleInput = ()=>{
        site_input = JSON.parse(localStorage.getItem("site_input"));
        for(const key in site_input["usage_fee"]){
            //site_input["usage_fee"][key] = (inputValues=={})?site_input["usage_fee"][key]:inputValues[key][key];
            console.log(inputValues.length())
            site_input["usage_fee"][key] = (inputValues.length == 0)?site_input["usage_fee"][key]:inputValues[key][key];
        }
        site_input["investment"]        = inputValues["investment"]["investment"];
        site_input["mount"]             = inputValues["mount"]["mount"];
        site_input["carrying_capacity"] = inputValues["carrying_capacity"]["carrying_capacity"];
        site_input["current_city"]      = inputValues["current_city"]["current_city"];
        site_input                      = JSON.stringify(site_input);
        localStorage.setItem("site_input",site_input);
    };
    return (
        <>
            <div className='container-fluid p-0 me-0 ms-0 input-menu-style'>
                <div className='row d-flex justify-content-center '>
                    <div className='col-lg d-flex justify-content-center' key={"search"}>
                        <input 
                            name='carrying_capacity'
                            value={inputValues["carrying_capacity"]?.carrying_capacity || site_input["carrying_capacity"] || ''}
                            onChange={(e) => handleInputChange(e, "carrying_capacity")}
                            type='text' 
                            className='albion-inp-field' 
                            placeholder='Carrying Capacity'/>
                    </div>  
                        
                    <div className='col-lg d-flex justify-content-center' key={"investment"}>
                        <input 
                            name='investment'
                            value={inputValues["investment"]?.investment || site_input["investment"] || ''}
                            onChange={(e) => handleInputChange(e, "investment")}
                            type='text' 
                            className='albion-inp-field' 
                            placeholder='Investment' 
                            aria-label='Search' 
                            aria-describedby='basic-addon2'/>
                    </div>

                    <div className='col-lg d-flex justify-content-center'>
                        <div className='flex-column d-flex' key={"usage-fee"}> 
                        <CollapseMenu
                            id="usage-fee"
                            text="Usage Fee"
                            className_btn = "albion-inp-field p-0">          
                                <input 
                                    name='tanner'
                                    value={inputValues["tanner"]?.tanner || site_input["usage_fee"]["tanner"] || ''}
                                    onChange={(e) => handleInputChange(e, "tanner")}
                                    type='text' 
                                    className='form-control' 
                                    placeholder='Investment' 
                                    aria-label='Search' 
                                    aria-describedby='basic-addon2'/>
                                <input 
                                    name='weaver'
                                    value={inputValues["weaver"]?.weaver || site_input["usage_fee"]["weaver"] || ''}
                                    onChange={(e) => handleInputChange(e, "weaver")}
                                    type='text' 
                                    className='form-control' 
                                    placeholder='Investment' 
                                    aria-label='Search' 
                                    aria-describedby='basic-addon2'/>
                                <input 
                                    name='smelter'
                                    value={inputValues["smelter"]?.smelter || site_input["usage_fee"]["smelter"] || ''}
                                    onChange={(e) => handleInputChange(e, "smelter")}
                                    type='text' 
                                    className='form-control' 
                                    placeholder='Investment' 
                                    aria-label='Search' 
                                    aria-describedby='basic-addon2'/>
                                <input 
                                    name='lumbermill'
                                    value={inputValues["lumbermill"]?.lumbermill || site_input["usage_fee"]["lumbermill"] || ''}
                                    onChange={(e) => handleInputChange(e, "lumbermill")}
                                    type='text' 
                                    className='form-control' 
                                    placeholder='Investment' 
                                    aria-label='Search' 
                                    aria-describedby='basic-addon2'/>
                                <input 
                                    name='ToolMaker'
                                    value={inputValues["ToolMaker"]?.ToolMaker || site_input["usage_fee"]["ToolMaker"] || ''}
                                    onChange={(e) => handleInputChange(e, "ToolMaker")}
                                    type='text' 
                                    className='form-control' 
                                    placeholder='Investment' 
                                    aria-label='Search' 
                                    aria-describedby='basic-addon2'/>
                                <input 
                                    name='warrior'
                                    value={inputValues["warrior"]?.warrior || site_input["usage_fee"]["warrior"] || ''}
                                    onChange={(e) => handleInputChange(e, "warrior")}
                                    type='text' 
                                    className='form-control' 
                                    placeholder='Investment' 
                                    aria-label='Search' 
                                    aria-describedby='basic-addon2'/>
                                <input 
                                    name='hunter'
                                    value={inputValues["hunter"]?.hunter || site_input["usage_fee"]["hunter"] || ''}
                                    onChange={(e) => handleInputChange(e, "hunter")}
                                    type='text' 
                                    className='form-control' 
                                    placeholder='Investment' 
                                    aria-label='Search' 
                                    aria-describedby='basic-addon2'/>
                                <input 
                                    name='mage'
                                    value={inputValues["mage"]?.mage || site_input["usage_fee"]["mage"] || ''}
                                    onChange={(e) => handleInputChange(e, "mage")}
                                    type='text' 
                                    className='form-control' 
                                    placeholder='Investment' 
                                    aria-label='Search' 
                                    aria-describedby='basic-addon2'/>
                                
                        </CollapseMenu>    
                        </div>
                    </div> 

                    <div className='col-lg d-flex justify-content-center'>
                        <select 
                            name='mount'
                            value={inputValues["mount"]?.mount || site_input["mount"] || ''}
                            onChange={(e) => handleInputChange(e, "mount")}
                            class="albion-inp-field" 
                            key={"mount"}>
                            <option selected value="Ignore carry weight">Ignore carry weight</option>
                            <option value="Expert's Transport Ox">Expert's Transport Ox</option>
                            <option value="Master's Transport Ox">Master's Transport Ox</option>
                            <option value="Grandmaster's Transport Ox">Grandmaster's Transport Ox</option>
                            <option value="Elder's Transport Ox">Elder's Transport Ox</option>
                            <option value="Saddled Direboar">Saddled Direboar</option>
                        </select> 
                    </div>

                    <div className='col-lg d-flex justify-content-center'>
                        <select 
                            name='current_city'
                            value={inputValues["current_city"]?.current_city || site_input["current_city"] || ''}
                            onChange={(e) => handleInputChange(e, "current_city")}
                            class="albion-inp-field">  
                            <option selected value="Caerleon">Caerleon</option>
                            <option value="Lymhurst">Lymhurst</option>
                            <option value="Bridgewatch">Bridgewatch</option>
                            <option value="Martlock">Martlock</option>
                            <option value="Thetford">Thetford</option>
                            <option value="Forst Sterling">Forst Sterling</option>
                        </select> 
                    </div> 
                    <div className='col-lg d-flex justify-content-center'>
                        <button className='albion-calculate-btn' onClick={handleInput}/>
                    </div>
                </div>
            </div>
        </>

    );
}
